import React, { useState, useEffect } from "react";
import { Button, Form, Row, Col } from "react-bootstrap";
import { PencilFill } from "react-bootstrap-icons";
import axios from 'axios';  // Import axios for API requests
import { ip } from "../../../ContentExport";
const DoctorBiography = ({ biography = {}, did, handleUpdateBiography }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    personalStatement: biography.personalStatement || '',
    education: biography.education || [],
    certifications: biography.certifications || [],
    workExperience: biography.workExperience || [],
    achievements: biography.achievements || [],
    researchInterests: biography.researchInterests || [],
  });

  // Only update formData if biography changes
  useEffect(() => {
    setFormData({
      personalStatement: biography.personalStatement || '',
      education: biography.education || [],
      certifications: biography.certifications || [],
      workExperience: biography.workExperience || [],
      achievements: biography.achievements || [],
      researchInterests: biography.researchInterests || [],
    });
  }, [biography]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(`/doctor/${did}/biography`, { biography: formData });
      handleUpdateBiography(response.data.updatedDoctor.biography); // Update parent state if necessary
      setIsEditing(false);
    } catch (error) {
      console.error("Error updating biography:", error);
    }
  };

  return (
    <div className="biography-container shadow-sm p-3">
      <div className="d-flex justify-content-between align-items-center">
        <h4>Biography</h4>
        <Button variant="primary" onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? "Cancel" : (
            <>
              <PencilFill size={14} style={{ marginRight: "0.5rem" }} />
              Edit Biography
            </>
          )}
        </Button>
      </div>

      {isEditing ? (
        <Form>
          <Form.Group controlId="personalStatement">
            <Form.Label>Personal Statement</Form.Label>
            <Form.Control
              as="textarea"
              name="personalStatement"
              value={formData.personalStatement}
              onChange={handleChange}
            />
          </Form.Group>

          {/* Education Fields */}
          {formData.education.map((edu, index) => (
            <Row key={index}>
              <Col>
                <Form.Group controlId={`degree-${index}`}>
                  <Form.Label>Degree</Form.Label>
                  <Form.Control
                    type="text"
                    name={`degree-${index}`}
                    value={edu.degree}
                    onChange={(e) => handleChange(e, index, 'education', 'degree')}
                  />
                </Form.Group>
              </Col>
              {/* Institution, Year, etc. */}
            </Row>
          ))}

          <Button variant="success" onClick={handleSave}>
            Save
          </Button>
        </Form>
      ) : (
        <div>
          <p><strong>Personal Statement:</strong> {formData.personalStatement}</p>
          {/* Display education, certifications, workExperience, etc. */}
        </div>
      )}
    </div>
  );
};

export default DoctorBiography;
