import React, { useState, useEffect } from "react";
import { Button, Form, Row, Col } from "react-bootstrap";
import { PencilFill } from "react-bootstrap-icons";

const DoctorBiography = ({ biography, did, handleUpdateBiography }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState(biography || {
    personalStatement: '',
    education: [],
    certifications: [],
    workExperience: [],
    achievements: [],
    researchInterests: []
  });

  useEffect(() => {
    setFormData(biography); // Update form data when biography prop changes
  }, [biography]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    handleUpdateBiography(formData); // Pass the updated biography data to parent
    setIsEditing(false);
  };

  return (
    <div className="biography-container shadow-sm p-3">
      <div className="d-flex justify-content-between align-items-center">
        <h4>Biography</h4>
        <Button variant="primary" onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? "Cancel" : (
            <>
              <PencilFill size={14} style={{ marginRight: "0.5rem" }} />
              Edit Biography
            </>
          )}
        </Button>
      </div>

      {isEditing ? (
        <Form>
          <Form.Group controlId="personalStatement">
            <Form.Label>Personal Statement</Form.Label>
            <Form.Control
              as="textarea"
              name="personalStatement"
              value={formData.personalStatement || ''}
              onChange={handleChange}
            />
          </Form.Group>

          <h5>Education</h5>
          {formData.education.map((edu, index) => (
            <Row key={index}>
              <Col>
                <Form.Group controlId={`degree-${index}`}>
                  <Form.Label>Degree</Form.Label>
                  <Form.Control
                    type="text"
                    name={`degree-${index}`}
                    value={edu.degree}
                    onChange={(e) => handleChange(e, index, 'education', 'degree')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`institution-${index}`}>
                  <Form.Label>Institution</Form.Label>
                  <Form.Control
                    type="text"
                    name={`institution-${index}`}
                    value={edu.institution}
                    onChange={(e) => handleChange(e, index, 'education', 'institution')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`year-${index}`}>
                  <Form.Label>Year</Form.Label>
                  <Form.Control
                    type="number"
                    name={`year-${index}`}
                    value={edu.year}
                    onChange={(e) => handleChange(e, index, 'education', 'year')}
                  />
                </Form.Group>
              </Col>
            </Row>
          ))}

          <h5>Certifications</h5>
          {formData.certifications.map((cert, index) => (
            <Row key={index}>
              <Col>
                <Form.Group controlId={`certification-${index}`}>
                  <Form.Label>Certification</Form.Label>
                  <Form.Control
                    type="text"
                    name={`certification-${index}`}
                    value={cert.certification}
                    onChange={(e) => handleChange(e, index, 'certifications', 'certification')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`issuingOrganization-${index}`}>
                  <Form.Label>Issuing Organization</Form.Label>
                  <Form.Control
                    type="text"
                    name={`issuingOrganization-${index}`}
                    value={cert.issuingOrganization}
                    onChange={(e) => handleChange(e, index, 'certifications', 'issuingOrganization')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`year-${index}`}>
                  <Form.Label>Year</Form.Label>
                  <Form.Control
                    type="number"
                    name={`year-${index}`}
                    value={cert.year}
                    onChange={(e) => handleChange(e, index, 'certifications', 'year')}
                  />
                </Form.Group>
              </Col>
            </Row>
          ))}

          {/* Add sections for Work Experience, Achievements, Research Interests similarly */}

          <Button variant="success" onClick={handleSave}>
            Save
          </Button>
        </Form>
      ) : (
        <div>
          <p><strong>Personal Statement:</strong> {formData.personalStatement}</p>
          <h5>Education</h5>
          <ul>
            {formData.education.map((edu, index) => (
              <li key={index}>{edu.degree} from {edu.institution} ({edu.year})</li>
            ))}
          </ul>

          <h5>Certifications</h5>
          <ul>
            {formData.certifications.map((cert, index) => (
              <li key={index}>{cert.certification} from {cert.issuingOrganization} ({cert.year})</li>
            ))}
          </ul>

          {/* Add similar display for Work Experience, Achievements, Research Interests */}
        </div>
      )}
    </div>
  );
};

export default DoctorBiography;
