import React, { useState, useEffect } from "react";
import { Button, Form, Row, Col } from "react-bootstrap";
import { PencilFill } from "react-bootstrap-icons";
import axios from 'axios';  // Import axios for API requests
import { ip } from "../../../ContentExport";

const DoctorBiography = ({ biography = {}, did }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    personalStatement: '',
    education: [],
    certifications: [],
    workExperience: [],
    achievements: [],
    researchInterests: [],
  });

  // Fetch biography when the component mounts
  useEffect(() => {
    const fetchBiography = async () => {
      try {
        const response = await axios.get(`${ip.address}/doctor/${did}/getbiography`);
        setFormData(response.data.biography);
      } catch (error) {
        console.error('Error fetching biography:', error);
      }
    };

    fetchBiography();
  }, [did]); // Ensure this effect only runs once when the component mounts

  // Handle input changes
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle saving biography
  const handleSave = async () => {
    try {
      const response = await axios.put(`${ip.address}/doctor/${did}/updatebiography`, { biography: formData });
      setFormData(response.data.updatedDoctor.biography); // Update the form with the new biography
      setIsEditing(false);
    } catch (error) {
      console.error("Error updating biography:", error);
    }
  };

  // Handle deleting biography
  const handleDelete = async () => {
    try {
      await axios.delete(`${ip.address}/doctor/${did}/deletebiography`);
      setFormData({
        personalStatement: '',
        education: [],
        certifications: [],
        workExperience: [],
        achievements: [],
        researchInterests: [],
      }); // Reset form data after deletion
    } catch (error) {
      console.error("Error deleting biography:", error);
    }
  };

  return (
    <div className="biography-container shadow-sm p-3">
      <div className="d-flex justify-content-between align-items-center">
        <h4>Biography</h4>
        <Button variant="primary" onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? "Cancel" : (
            <>
              <PencilFill size={14} style={{ marginRight: "0.5rem" }} />
              Edit Biography
            </>
          )}
        </Button>
      </div>

      {isEditing ? (
        <Form>
          <Form.Group controlId="personalStatement">
            <Form.Label>Personal Statement</Form.Label>
            <Form.Control
              as="textarea"
              name="personalStatement"
              value={formData.personalStatement}
              onChange={handleChange}
            />
          </Form.Group>

          {/* Education Fields */}
          {formData.education.map((edu, index) => (
            <Row key={index}>
              <Col>
                <Form.Group controlId={`degree-${index}`}>
                  <Form.Label>Degree</Form.Label>
                  <Form.Control
                    type="text"
                    name={`degree-${index}`}
                    value={edu.degree}
                    onChange={(e) => handleChange(e, index, 'education', 'degree')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`institution-${index}`}>
                  <Form.Label>Institution</Form.Label>
                  <Form.Control
                    type="text"
                    name={`institution-${index}`}
                    value={edu.institution}
                    onChange={(e) => handleChange(e, index, 'education', 'institution')}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId={`year-${index}`}>
                  <Form.Label>Year</Form.Label>
                  <Form.Control
                    type="number"
                    name={`year-${index}`}
                    value={edu.year}
                    onChange={(e) => handleChange(e, index, 'education', 'year')}
                  />
                </Form.Group>
              </Col>
            </Row>
          ))}

          <Button variant="success" onClick={handleSave}>
            Save
          </Button>
          <Button variant="danger" onClick={handleDelete} className="ml-3">
            Delete Biography
          </Button>
        </Form>
      ) : (
        <div>
          <p><strong>Personal Statement:</strong> {formData.personalStatement}</p>
          <h5>Education</h5>
          <ul>
            {formData.education.length > 0 ? (
              formData.education.map((edu, index) => (
                <li key={index}>{edu.degree} from {edu.institution} ({edu.year})</li>
              ))
            ) : (
              <li>No education details available.</li>
            )}
          </ul>

          <h5>Certifications</h5>
          <ul>
            {formData.certifications.length > 0 ? (
              formData.certifications.map((cert, index) => (
                <li key={index}>{cert.certification} from {cert.issuingOrganization} ({cert.year})</li>
              ))
            ) : (
              <li>No certification details available.</li>
            )}
          </ul>

          <h5>Work Experience</h5>
          <ul>
            {formData.workExperience.length > 0 ? (
              formData.workExperience.map((exp, index) => (
                <li key={index}>{exp.position} at {exp.organization} ({new Date(exp.startDate).getFullYear()} - {exp.endDate ? new Date(exp.endDate).getFullYear() : 'Present'})</li>
              ))
            ) : (
              <li>No work experience details available.</li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
};

export default DoctorBiography;
