import React, { useState, useEffect } from "react";
import { Button, Form, Row, Col, Container } from "react-bootstrap";
import axios from "axios";
import ImageUploadModal from "./modal/ImageUploadModal";
import UpdateInfoModal from "./modal/UpdateInfoModal";
import DoctorBiography from "./DoctorBiography";  // Import the new Biography component
import { PencilFill } from "react-bootstrap-icons";
import { useLocation } from "react-router-dom";
useLocation
const AccountInfo = () => {
  const location = useLocation();
  const { did } = location.state || {};
  const [doctorData, setDoctorData] = useState({
    theId: "",
    theName: "",
    theImage: "images/014ef2f860e8e56b27d4a3267e0a193a.jpg", 
    theLastName: "",
    theMI: "",
    email: "",
    cnumber: "",
    password: "",
    dob: "",
    specialty: "",
    biography: {} // Add biography field to doctorData
  });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:8000/doctor/api/finduser/${did}`)
      .then((res) => {
        const data = res.data.theDoctor;
        setDoctorData({
          ...doctorData,
          theId: data._id,
          theName: data.dr_firstName,
          theImage: data.dr_image || doctorData.theImage,
          theLastName: data.dr_lastName,
          theMI: data.dr_middleInitial,
          email: data.dr_email,
          cnumber: data.dr_contactNumber,
          dob: data.dr_dob,
          password: data.dr_password,
          specialty: data.dr_specialty,
          biography: data.biography || {} // Get biography from the response
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }, [did]);

  const handleUpdateBiography = (updatedBiography) => {
    const updatedData = { ...doctorData, biography: updatedBiography };
    axios.put(`http://localhost:8000/doctor/api/${did}/updateDetails`, updatedData)
      .then((response) => {
        setDoctorData({
          ...doctorData,
          biography: updatedBiography
        });
      })
      .catch((error) => {
        console.error('Error updating biography:', error);
      });
  };

  return (
    <>
      <Container fluid className="d-flex flex-column align-items-center">
        {/* Doctor Info */}
        <div className="ai-container d-flex align-items-center shadow-sm">
          <img src={`http://localhost:8000/${doctorData.theImage}`} alt="Doctor" className="ai-image" />
          <div className="ml-4">
            <h4>{`${doctorData.theName} ${doctorData.theMI}. ${doctorData.theLastName}`}</h4>
            <p>{doctorData.specialty}</p>
            <Button onClick={() => setIsModalOpen(true)}>
              Upload Image <PencilFill />
            </Button>
          </div>
        </div>

        {/* Doctor Biography */}
        <DoctorBiography
          biography={doctorData.biography}
          did={doctorData.theId}
          handleUpdateBiography={handleUpdateBiography}
        />
      </Container>

      <ImageUploadModal 
        isOpen={isModalOpen}
        onRequestClose={(newImageUrl) => setIsModalOpen(false)}
        did={did}
      />
      
      <UpdateInfoModal
        show={isUpdateModalOpen}
        handleClose={() => setIsUpdateModalOpen(false)}
        doctorData={doctorData}
        handleUpdate={(updatedData) => setDoctorData(updatedData)}
      />
    </>
  );
};

export default AccountInfo;
