import React, { useState, useCallback } from 'react';
import Modal from 'react-modal';
import Cropper from 'react-easy-crop';
import { Button, Form } from "react-bootstrap";
import './UploadImageModal.css';

const CropResizeTiltModal = ({ isOpen, onRequestClose, imageSrc, onSave }) => {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [loading, setLoading] = useState(false); // Track if save operation is in progress

  const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
    setCroppedAreaPixels(croppedAreaPixels);  // Save cropped area for use in image processing
    console.log('Cropped Area Pixels:', croppedAreaPixels);  // Debugging to ensure cropped area is captured
  }, []);

  const createImage = (url) =>
    new Promise((resolve, reject) => {
      const image = new Image();
      image.addEventListener('load', () => resolve(image));
      image.addEventListener('error', (error) => reject(error));
      image.setAttribute('crossOrigin', 'anonymous');  // Prevent CORS issues
      image.src = url;
    });

  const getCroppedImg = async (imageSrc, croppedAreaPixels, rotation = 0) => {
    try {
      const image = await createImage(imageSrc);
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // Set canvas dimensions to the cropped area
      canvas.width = croppedAreaPixels.width;
      canvas.height = croppedAreaPixels.height;

      // Translate canvas and rotate
      ctx.translate(canvas.width / 2, canvas.height / 2);
      ctx.rotate((rotation * Math.PI) / 180);
      ctx.translate(-canvas.width / 2, -canvas.height / 2);
      
      // Draw image onto canvas
      ctx.drawImage(
        image,
        croppedAreaPixels.x,
        croppedAreaPixels.y,
        croppedAreaPixels.width,
        croppedAreaPixels.height,
        0,
        0,
        canvas.width,
        canvas.height
      );

      // Return the cropped image as a base64 string
      return new Promise((resolve) => {
        canvas.toBlob((blob) => {
          const fileReader = new FileReader();
          fileReader.readAsDataURL(blob);
          fileReader.onloadend = () => {
            console.log('Cropped Image (Base64):', fileReader.result);  // Debugging to ensure the base64 image is generated
            resolve(fileReader.result);  // Return the base64 image
          };
        });
      });
    } catch (error) {
      console.error('Error cropping image:', error);
      throw new Error('Failed to crop image');
    }
  };

  const handleSave = async () => {
    if (!croppedAreaPixels) {
      console.error('No cropped area detected!');  // Ensure the cropping is done before saving
      return;
    }

    setLoading(true); // Set loading state
    try {
      const croppedImageSrc = await getCroppedImg(imageSrc, croppedAreaPixels, rotation);
      console.log('Saving cropped image...');  // Debugging to ensure saving is in progress
      onSave(croppedImageSrc);  // Pass the cropped image back to the parent component
      onRequestClose();  // Close the modal after saving
    } catch (error) {
      console.error('Failed to save cropped image:', error);  // Handle any errors that occur
    } finally {
      setLoading(false);  // Reset loading state
    }
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Edit Image"
      className="image-edit-modal"
      overlayClassName="modal-overlay"
      ariaHideApp={false}
    >
      <div className="modal-content">
        <h2>Edit Image</h2>
        <div className="crop-container">
          <Cropper
            image={imageSrc}
            crop={crop}
            zoom={zoom}
            rotation={rotation}
            aspect={1}
            cropShape="round"
            showGrid={false}
            onCropChange={setCrop}
            onZoomChange={setZoom}
            onRotationChange={setRotation}
            onCropComplete={onCropComplete}  // Ensure crop completion is captured
          />
        </div>

        <div className="controls">
          <Form.Label>Zoom</Form.Label>
          <Form.Control
            type="range"
            value={zoom}
            min="1"
            max="3"
            step="0.1"
            aria-labelledby="Zoom"
            onChange={(e) => setZoom(e.target.value)}
          />
          <Form.Label>Rotation</Form.Label>
          <Form.Control
            type="range"
            value={rotation}
            min="0"
            max="360"
            step="1"
            aria-labelledby="Rotation"
            onChange={(e) => setRotation(e.target.value)}
          />
        </div>

        <Button 
          style={{ marginTop: "15px" }} 
          onClick={handleSave} 
          disabled={loading}  // Disable button while saving
        >
          {loading ? 'Saving...' : 'Save'}
        </Button>
      </div>
    </Modal>
  );
};

export default CropResizeTiltModal;
