// UpdateInfoModal.jsx
import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';
ip

const UpdateInfoModal = ({ show, handleClose, doctorData, handleUpdate }) => {
    const [formData, setFormData] = useState({
        ms_firstName: doctorData.theName,
        ms_lastName: doctorData.theLastName,
        ms_middleInitial: doctorData.theMI,
        ms_email: doctorData.email,
        ms_contactNumber: doctorData.cnumber,
        ms_specialty: doctorData.specialty,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.put(`${ip.address}/medicalsecretary/api/${doctorData.theId}/updateDetails`, formData)
            .then((response) => {
                handleUpdate(response.data.updatedMedicalSecretary);
                handleClose(); // Close the modal after the update
            })
            .catch((error) => {
                console.error('Error updating Medical Secretary information:', error);
            });
    };

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Update Information</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="firstName">
                        <Form.Label>First Name</Form.Label>
                        <Form.Control
                            type="text"
                            name="ms_firstName"
                            value={formData.ms_firstName}
                            onChange={handleChange}
                            required
                        />
                    </Form.Group>
                    <Form.Group controlId="lastName">
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control
                            type="text"
                            name="ms_lastName"
                            value={formData.ms_lastName}
                            onChange={handleChange}
                            required
                        />
                    </Form.Group>
                    <Form.Group controlId="middleInitial">
                        <Form.Label>Middle Initial</Form.Label>
                        <Form.Control
                            type="text"
                            name="ms_middleInitial"
                            value={formData.ms_middleInitial}
                            onChange={handleChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            type="email"
                            name="ms_email"
                            value={formData.ms_email}
                            onChange={handleChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="contactNumber">
                        <Form.Label>Contact Number</Form.Label>
                        <Form.Control
                            type="text"
                            name="ms_contactNumber"
                            value={formData.ms_contactNumber}
                            onChange={handleChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="specialty">
                        <Form.Label>Specialty</Form.Label>
                        <Form.Control
                            type="text"
                            name="ms_specialty"
                            value={formData.ms_specialty}
                            onChange={handleChange}
                        />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Update
                    </Button>
                </Form>
            </Modal.Body>
        </Modal>
    );
};

export default UpdateInfoModal;
