import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Row, Form, Col, Button, Container, Modal } from 'react-bootstrap';
import './LogIn.css';
import NavigationalBar from '../landpage/navbar';
import { ip } from '../../ContentExport';
import { usePatient } from '../patient/PatientContext';
import { useDoctor } from '../practitioner/DoctorContext';
const LogInUser = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userRole, setUserRole] = useState("Patient");
  const [rememberMe, setRememberMe] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [modalEmail, setModalEmail] = useState("");
  const [modalRole, setModalRole] = useState("Patient");
  const [modalMessage, setModalMessage] = useState("");

  const { setPatient } = usePatient();
  const { setDoctor } = useDoctor();
  const loginuser = async (e) => {
    e.preventDefault();

    if (userRole === "Patient") {
      // Patient login
      try {
        const response = await axios.post(`${ip.address}/patient/api/login`, {
          email,
          password,
        });

        if (response.data.patientData) {
          const patientData = response.data.patientData;

          // Handle "Remember Me"
          if (rememberMe) {
            localStorage.setItem('patient', JSON.stringify(patientData));
          } else {
            sessionStorage.setItem('patient', JSON.stringify(patientData));
          }

          setPatient(patientData); // Update context with patient data
          window.alert("Successfully logged in");

          // Navigate to homepage
          await axios.post(`${ip.address}/api/patient/session`, { userId: patientData._id, role: userRole });
          navigate('/homepage');
        } else {
          window.alert(response.data.message || "Invalid email or password. Please try again.");
        }
      } catch (err) {
        console.error('Error logging in:', err);
        window.alert("An error occurred while logging in.");
      }
    } else if (userRole === "Physician") {
      // Physician login
      try {
        const response = await axios.post(`${ip.address}/doctor/api/login`, {
          email,
          password,
        });

        if (response.data.doctorData) {
          const doctorData = response.data.doctorData;

          // Handle "Remember Me"
          if (rememberMe) {
            localStorage.setItem('doctor', JSON.stringify(doctorData));
          } else {
            sessionStorage.setItem('doctor', JSON.stringify(doctorData));
          }

          setDoctor(doctorData); // Update context with doctor data
          // setDoctor(doctorData); // Assuming you have a context for doctor
          window.alert("Successfully logged in");

          // Navigate to dashboard
          await axios.post(`${ip.address}/api/doctor/session`, { userId: doctorData._id, role: userRole });
          navigate('/dashboard');
        } else {
          window.alert(response.data.message || "Invalid email or password. Please try again.");
        }
      } catch (err) {
        console.error('Error logging in:', err);
        window.alert("An error occurred while logging in.");
      }
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();

    if (modalRole === "Patient") {
      // Forgot password for Patient
      try {
        const response = await axios.post(`${ip.address}/patient/api/forgot-password`, { email: modalEmail });
        setModalMessage(response.data.message);
      } catch (err) {
        console.error('Error in forgot password:', err);
        setModalMessage(err.response?.data?.message || 'An error occurred.');
      }
    } else if (modalRole === "Physician") {
      // Forgot password for Physician
      setModalMessage("Forgot password functionality for Physician is not implemented yet.");
    }
  };

  return (
    <>
      <NavigationalBar />
      <div className="align-items-center d-flex vh-100">
        <Container fluid className="maincontainer d-flex justify-content-center align-items-center">
          <div className="container">
            <h1>Welcome Back!</h1>
            <Form onSubmit={loginuser}>
              <Row className="mb-3">
                <Form.Group className="mb-3" controlId="formEmail">
                  <Form.Label>Email Address</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter Email Address"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Form.Group>
              </Row>
              <Row className="align-items right">
                <Form.Group className="mb-3" controlId="formPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </Form.Group>
              </Row>
              <Row>
                <Form.Group as={Col} controlId="formChoose">
                  <Form.Label>Choose your role:</Form.Label>
                  <Form.Select value={userRole} onChange={(e) => setUserRole(e.target.value)}>
                    <option value="Patient">Patient</option>
                    <option value="Physician">Physician</option>
                  </Form.Select>
                </Form.Group>
              </Row>
              <Row className="mb-3"></Row>
              <Row className="mb-3">
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label="Remember me"
                    checked={rememberMe}
                    onChange={(e) => setRememberMe(e.target.checked)}
                  />
                </Form.Group>
              </Row>
              <div className="justify-content-center">
                <Button type="submit" className="mb-2 buttonStyle">
                  Log In
                </Button>
              </div>
              <div className="mb-0">
                <a href="#" onClick={() => setShowModal(true)}>Forgot Password?</a>
              </div>
              <div className="mb-0">
                <a href="/medapp/signup">No account yet? Sign up</a>
              </div>
            </Form>
          </div>
        </Container>
      </div>

      {/* Forgot Password Modal */}
      <Modal show={showModal} onHide={() => {
        setShowModal(false);
        setModalMessage('');
      }}>
        <Modal.Header closeButton>
          <Modal.Title>Forgot Password</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {modalMessage ? (
            <p>{modalMessage}</p>
          ) : (
            <Form onSubmit={handleForgotPassword}>
              <Form.Group className="mb-3" controlId="formModalEmail">
                <Form.Label>Email Address</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter Email Address"
                  value={modalEmail}
                  onChange={(e) => setModalEmail(e.target.value)}
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formModalRole">
                <Form.Label>Choose your role:</Form.Label>
                <Form.Select value={modalRole} onChange={(e) => setModalRole(e.target.value)}>
                  <option value="Patient">Patient</option>
                  <option value="Physician">Physician</option>
                </Form.Select>
              </Form.Group>
              <Button variant="primary" type="submit" className="mt-3">
                Submit
              </Button>
            </Form>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default LogInUser;
