import React, { useState } from 'react';
import MedSecNavbar from '../../navbar/MedSecNavbar';
import { Container, Row } from 'react-bootstrap';
import DoctorCards from './DoctorCards';
import DeactivationRequests from '../../../admin/appointment/doctors/DeactivationRequests';
import Nav from 'react-bootstrap/Nav';

function AllDoctors() {
  const [activeTab, setActiveTab] = useState('doctorCards'); // Default to 'doctorCards'

  return (
    <div style={{ display: 'flex', flexDirection: 'row', height: '100vh' }}>
      
      <div style={{ flex: 1 }}>
      <div style={{ width: '200px', backgroundColor: '#f8f9fa', paddingTop: '1rem', paddingLeft: '1rem' }}>
        {/* Sidebar for navigation */}
        <Nav className='flex-column' activeKey={activeTab} onSelect={(tab) => setActiveTab(tab)}>
          <Nav.Item style={{ marginBottom: '1rem' }}>
            <Nav.Link eventKey="doctorCards" style={{ fontWeight: activeTab === 'doctorCards' ? 'bold' : 'normal', color: '#000', cursor: 'pointer' }}>Doctors</Nav.Link>
          </Nav.Item>
          <Nav.Item style={{ marginBottom: '1rem' }}>
            <Nav.Link eventKey="deactivationRequests" style={{ fontWeight: activeTab === 'deactivationRequests' ? 'bold' : 'normal', color: '#000', cursor: 'pointer' }}>Appointment Deactivation Requests</Nav.Link>
          </Nav.Item>
        </Nav>
      </div>

        <MedSecNavbar />
        <Container fluid style={{ overflowY: 'auto', height: 'calc(100vh - 100px)', width: '100%', paddingBottom: '1.5rem' }}>
          <div style={{ paddingLeft: '3rem', paddingRight: '3rem' }} className='pt-5'>
            {/* Conditional rendering based on the active tab */}
            <Container>
              <Row style={{ marginTop: '20px', marginBottom: '20px' }}>
                {activeTab === 'doctorCards' && (
                  <DoctorCards />
                )}
                {activeTab === 'deactivationRequests' && (
                  <DeactivationRequests />
                )}
              </Row>
            </Container>
          </div>
        </Container>
      </div>
    </div>
  );
}

export default AllDoctors;
