import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Row, Button } from 'react-bootstrap';
import MedSecNavbar from '../../navbar/MedSecNavbar';
import UpdateInfoModal from './Modals/UpdateInfoModal';


const MedSecAccInfo = () => {
    const location = useLocation(); 
    const { userId, userName, role } = location.state || {};
    console.log(userId, userName, role);

    const [activeTab, setActiveTab] = useState('info');
    const [showUpdateModal, setShowUpdateModal] = useState(false);
    const [medicalSecretaryData, setMedicalSecretaryData] = useState({}); // State to store Medical Secretary data

    const handleUpdate = (updatedData) => {
        setMedicalSecretaryData(updatedData);
        // Optionally, update the state here or fetch the updated data again
    };

    return (
        <div>
            <MedSecNavbar msid={userId} />
            <Container fluid style={{ overflowY: 'auto', height: 'calc(100vh - 100px)', width: '100%', paddingBottom: '1.5rem' }}>
                <div className='maincolor-container'>
                    <div className='content-area'>
                        <Container className="d-flex justify-content-start ">
                            <Row>
                                <div className="horizontal-tabs">
                                    <a onClick={() => setActiveTab("info")} className={activeTab === "info" ? "active" : ""}>
                                        My details
                                    </a>
                                </div>    
                            </Row>
                        </Container>
                        <Container className='border-top'>
                            <Button onClick={() => setShowUpdateModal(true)}>Update Information</Button>
                            {/* Additional content for Medical Secretary's info here */}
                        </Container>
                    </div>
                </div>
            </Container>

            {/* Update Info Modal */}
            <UpdateInfoModal
                show={showUpdateModal}
                handleClose={() => setShowUpdateModal(false)}
                doctorData={medicalSecretaryData} // Pass current Medical Secretary data
                handleUpdate={handleUpdate} // Handle update in the parent
            />
        </div>
    );
};

export default MedSecAccInfo;
