import React from "react";
import { Carousel, Button, Dropdown, ButtonGroup } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisH, faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';

function DoctorAnnouncements({ posts, doctor_image, doctor_name }) {
  const defaultImage = "images/014ef2f860e8e56b27d4a3267e0a193a.jpg";

  return (
    <div className="shadow-sm">
      {posts.map((post, index) => (
        <div key={index} className="posted-announcement-container shadow-sm d-flex flex-column align-items-start p-4 mb-3 w-100">
          <div className="d-flex w-100 align-items-center justify-content-between">
            <div className="d-flex align-items-center">
              <img
                src={doctor_image ? doctor_image : defaultImage}
                alt="Doctor"
                style={{ width: "45px", height: "45px", borderRadius: "9999px" }}
              />
              <div style={{ paddingLeft: '0.5rem' }}>
                <span>{doctor_name}</span>
              </div>
            </div>

            {/* Optional actions dropdown */}
            <Dropdown as={ButtonGroup}>
              <Dropdown.Toggle variant="secondary" as="div" className="p-0 m-0 border-0 bg-transparent">
                <FontAwesomeIcon icon={faEllipsisH} />
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item>View</Dropdown.Item>
                {/* Additional options if needed */}
              </Dropdown.Menu>
            </Dropdown>
          </div>

          <div className="mt-2 w-100">
            <div className="px-4">
              <div dangerouslySetInnerHTML={{ __html: post.content }} />
            </div>

            <div className="w-100 p-0 m-0 no-gutters" style={{ position: 'relative', margin: '0 auto' }}>
              {post.images && post.images.length > 0 && (
                <Carousel interval={null} indicators={false} controls={true}>
                  {post.images.map((img, i) => (
                    <Carousel.Item key={i} className="d-flex justify-content-center">
                      <img
                        src={img}
                        alt="Post"
                        style={{ width: "100%", height: "600px", objectFit: "cover" }}
                      />
                    </Carousel.Item>
                  ))}
                </Carousel>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default DoctorAnnouncements;
