import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Row, Form, Col, Button, Container, Modal } from 'react-bootstrap';
import './LogIn.css'; // Add custom styles here
import NavigationalBar from '../landpage/navbar';
import { ip } from '../../ContentExport'; // Assuming your IP configuration is here
import { usePatient } from '../patient/PatientContext';
import { useDoctor } from '../practitioner/DoctorContext';

const LogInUser = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userRole, setUserRole] = useState("Patient");
  const [rememberMe, setRememberMe] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [modalEmail, setModalEmail] = useState("");
  const [modalRole, setModalRole] = useState("Patient");
  const [modalMessage, setModalMessage] = useState("");

  const { setPatient } = usePatient();
  const { setDoctor } = useDoctor();

  const loginuser = async (e) => {
    e.preventDefault();

    if (userRole === "Patient") {
      try {
        const response = await axios.post(`${ip.address}/patient/api/login`, { email, password });
        if (response.data.patientData) {
          const patientData = response.data.patientData;
          if (rememberMe) localStorage.setItem('patient', JSON.stringify(patientData));
          else sessionStorage.setItem('patient', JSON.stringify(patientData));

          setPatient(patientData);
          window.alert("Successfully logged in");
          await axios.post(`${ip.address}/api/patient/session`, { userId: patientData._id, role: userRole });
          navigate('/homepage');
        } else {
          window.alert(response.data.message || "Invalid email or password. Please try again.");
        }
      } catch (err) {
        console.error('Error logging in:', err);
        window.alert("An error occurred while logging in.");
      }
    } else if (userRole === "Physician") {
      try {
        const response = await axios.post(`${ip.address}/doctor/api/login`, { email, password });
        if (response.data.doctorData) {
          const doctorData = response.data.doctorData;
          if (rememberMe) localStorage.setItem('doctor', JSON.stringify(doctorData));
          else sessionStorage.setItem('doctor', JSON.stringify(doctorData));

          setDoctor(doctorData);
          window.alert("Successfully logged in");
          await axios.post(`${ip.address}/api/doctor/session`, { userId: doctorData._id, role: userRole });
          navigate('/dashboard');
        } else {
          window.alert(response.data.message || "Invalid email or password. Please try again.");
        }
      } catch (err) {
        console.error('Error logging in:', err);
        window.alert("An error occurred while logging in.");
      }
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    if (modalRole === "Patient") {
      try {
        const response = await axios.post(`${ip.address}/patient/api/forgot-password`, { email: modalEmail });
        setModalMessage(response.data.message);
      } catch (err) {
        console.error('Error in forgot password:', err);
        setModalMessage(err.response?.data?.message || 'An error occurred.');
      }
    } else if (modalRole === "Physician") {
      try {
        const response = await axios.post(`${ip.address}/doctor/api/forgot-password`, { email: modalEmail });
        setModalMessage(response.data.message);
      } catch (err) {
        console.error('Error in forgot password:', err);
        setModalMessage(err.response?.data?.message || 'An error occurred.');
      }
    }
  };

  return (
    <>
      <NavigationalBar />
      <Container fluid className="vh-100 d-flex">
        <Row className="w-100 align-items-center">
          {/* Left Column with Image */}
          <Col md={6} className="d-none d-md-block p-0">
            <div className="login-left-image" style={{ backgroundImage: `url(${ip.address}/images/Background-Login.png)` }} />
          </Col>

          {/* Right Column with Login Form */}
          <Col md={6} className="d-flex align-items-center justify-content-center">
            <div className="login-form-container">
              <div className="text-center mb-4">
                <img src={`${ip.address}/images/logo.png`} alt="Logo" className="mb-3" />
                <h3>Sign In</h3>
              </div>
              <Form onSubmit={loginuser}>
                <Form.Group controlId="formEmail" className="mb-3">
                  <Form.Control
                    type="email"
                    placeholder="Enter Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </Form.Group>
                <Form.Group controlId="formPassword" className="mb-3">
                  <Form.Control
                    type="password"
                    placeholder="Enter Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Select value={userRole} onChange={(e) => setUserRole(e.target.value)}>
                    <option value="Patient">Patient</option>
                    <option value="Physician">Physician</option>
                  </Form.Select>
                </Form.Group>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <Form.Check
                    type="checkbox"
                    label="Remember Me"
                    checked={rememberMe}
                    onChange={(e) => setRememberMe(e.target.checked)}
                  />
                  <a href="#" onClick={() => setShowModal(true)} className="text-primary">
                    Forgot Password?
                  </a>
                </div>
                <Button type="submit" variant="primary" className="w-100">Sign In</Button>
              </Form>
              <p className="text-center mt-3">Need an account? <a href="/medapp/signup" className="text-primary">Sign Up</a></p>
            </div>
          </Col>
        </Row>
      </Container>

      {/* Forgot Password Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Forgot Password</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {modalMessage ? (
            <p>{modalMessage}</p>
          ) : (
            <Form onSubmit={handleForgotPassword}>
              <Form.Group controlId="formModalEmail" className="mb-3">
                <Form.Control
                  type="email"
                  placeholder="Enter Email Address"
                  value={modalEmail}
                  onChange={(e) => setModalEmail(e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group controlId="formModalRole" className="mb-3">
                <Form.Select value={modalRole} onChange={(e) => setModalRole(e.target.value)}>
                  <option value="Patient">Patient</option>
                  <option value="Physician">Physician</option>
                </Form.Select>
              </Form.Group>
              <Button type="submit" variant="primary" className="w-100">Submit</Button>
            </Form>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default LogInUser;
