import axios from "axios";
import { useState, useEffect } from "react";
import { Row, Col, Button, Form, Modal } from 'react-bootstrap';
import Select from 'react-select'; // Import react-select

function AssignAppointmentModal({ show, handleClose, pid }) {
    const [doctors, setDoctors] = useState([]);
    const [selectedDoctor, setSelectedDoctor] = useState(null);
    const [doctorName, setDoctorName] = useState("");
    const [date, setDate] = useState("");
    const [time, setTime] = useState("");
    const [availableTimes, setAvailableTimes] = useState([]);
    const [availability, setAvailability] = useState({});
    const [activeAppointmentStatus, setActiveAppointmentStatus] = useState(true);

    // Fetch all doctors
    useEffect(() => {
        axios.get(`http://localhost:8000/doctor/api/alldoctors`)
            .then((response) => {
                const doctorOptions = response.data.theDoctor.map((doctor) => ({
                    value: doctor._id,
                    label: `${doctor.dr_firstName} ${doctor.dr_middleInitial}. ${doctor.dr_lastName}`,
                }));
                setDoctors(doctorOptions);
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);

    // Fetch doctor's availability based on the selected doctor
    useEffect(() => {
        if (selectedDoctor) {
            axios.get(`http://localhost:8000/doctor/${selectedDoctor.value}`)
                .then((response) => {
                    const doctor = response.data.doctor;
                    setDoctorName(`${doctor.dr_firstName} ${doctor.dr_middleInitial}. ${doctor.dr_lastName}`);
                    setAvailability(doctor.availability || {});
                    setActiveAppointmentStatus(doctor.activeAppointmentStatus);
                })
                .catch((err) => {
                    console.log(err);
                });
        } else {
            setDoctorName('No Doctor Selected');
            setAvailability({});
            setAvailableTimes([]);
        }
    }, [selectedDoctor]);

    // Fetch available time slots based on date and doctor
    useEffect(() => {
        if (date) {
            const selectedDate = new Date(date);
            const daysOfWeek = ["sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"];
            const day = daysOfWeek[selectedDate.getDay()];
            setAvailableTimes(getAvailableTimes(day));
        } else {
            setAvailableTimes([]);
        }
    }, [date, availability]);

    // Function to generate time intervals
    const generateTimeIntervals = (start, end, interval) => {
        const times = [];
        const [startHour, startMinute] = start.split(':').map(Number);
        const [endHour, endMinute] = end.split(':').map(Number);
        let currentTime = new Date(1970, 0, 1, startHour, startMinute);
        const endTime = new Date(1970, 0, 1, endHour, endMinute);

        while (currentTime <= endTime) {
            times.push(currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
            currentTime = new Date(currentTime.getTime() + interval * 60000);
        }

        return times;
    };

    const getAvailableTimes = (day) => {
        const dayAvailability = availability[day];
        if (!dayAvailability) return [];

        let times = [];
        if (dayAvailability.morning?.available) {
            const morningTimes = generateTimeIntervals(
                dayAvailability.morning.startTime,
                dayAvailability.morning.endTime,
                dayAvailability.morning.interval || 30
            );
            times = times.concat(morningTimes);
        }
        if (dayAvailability.afternoon?.available) {
            const afternoonTimes = generateTimeIntervals(
                dayAvailability.afternoon.startTime,
                dayAvailability.afternoon.endTime,
                dayAvailability.afternoon.interval || 30
            );
            times = times.concat(afternoonTimes);
        }
        return times;
    };

    const getTodayDate = () => {
        const today = new Date();
        const year = today.getFullYear();
        const month = String(today.getMonth() + 1).padStart(2, '0');
        const day = String(today.getDate()).padStart(2, '0');
        return `${year}-${month}-${day}`;
    };

    const todayDate = getTodayDate();

    // Handle appointment creation
    const createAppointment = () => {
        if (!date || !time) {
            window.alert("Please select a valid date and time.");
            return;
        }

        const formData = {
            doctor: selectedDoctor ? selectedDoctor.value : null,
            pid,
            date,
            time: time || null,
        };

        axios.post(`http://localhost:8000/patient/api/${pid}/createappointment`, formData)
            .then(() => {
                window.alert("Created an appointment!");
                window.location.reload();
            })
            .catch((err) => {
                if (err.response) {
                    console.log(err.response.data);
                    window.alert(`Error: ${err.response.data.message}`);
                } else {
                    console.log(err);
                    window.alert('An error occurred while creating the appointment.');
                }
            });
    };

    return (
        <Modal show={show} onHide={handleClose} className='am-overlay'>
            <div className="am-content">
                <Modal.Header className="am-header" closeButton>
                    <Modal.Title>Assign Appointment</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* Doctor Selection with react-select */}
                    <Form.Group as={Col} className="mb-3">
                        <Form.Label>Select Doctor</Form.Label>
                        <Select
                            options={doctors}
                            value={selectedDoctor}
                            onChange={(selected) => setSelectedDoctor(selected)}
                            placeholder="Search for a doctor"
                            isClearable={true} // Allows clearing the selection
                        />
                    </Form.Group>

                    {/* Date Selection */}
                    <Form.Group as={Col} className="mb-3">
                        <Form.Label>Date</Form.Label>
                        <Form.Control
                            type="date"
                            min={todayDate}
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                            required
                        />
                    </Form.Group>

                    {/* Time Selection */}
                    <Form.Group as={Col} className="mb-3">
                        <Form.Label>Time</Form.Label>
                        {availableTimes.length > 0 ? (
                            <div>
                                {availableTimes.map((timeSlot, index) => (
                                    <Button
                                        key={index}
                                        variant={time === timeSlot ? "secondary" : "outline-primary"}
                                        onClick={() => setTime(timeSlot)}
                                        className="m-1"
                                    >
                                        {timeSlot}
                                    </Button>
                                ))}
                            </div>
                        ) : (
                            <Form.Control
                                type="time"
                                value={time}
                                onChange={(e) => setTime(e.target.value)}
                                placeholder="Enter time"
                            />
                        )}
                    </Form.Group>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={createAppointment}>
                        Submit
                    </Button>
                </Modal.Footer>
            </div>
        </Modal>
    );
}

export default AssignAppointmentModal;
