import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';

const AssignAppointmentModal = ({ 
  show, 
  handleClose, 
  alldoctors, 
  selectedDoctor, 
  setSelectedDoctor, 
  selectedDate, 
  setSelectedDate, 
  selectedTime, 
  setSelectedTime, 
  handleSaveDetails, 
  error 
}) => {
  const [availableTimes, setAvailableTimes] = useState([]); // Available times for the selected doctor and date
  const [bookedTimes, setBookedTimes] = useState([]); // Booked times for the selected date
  const [manualTimeInput, setManualTimeInput] = useState(false); // Toggle between time slots and manual input

  // Fetch available times based on the selected doctor and date
  useEffect(() => {
    if (selectedDoctor && selectedDate) {
      // Fetch available times from the server
      axios.get(`http://localhost:8000/doctor/${selectedDoctor}/booked-slots?date=${selectedDate}`)
        .then((response) => {
          const { availableTimes, bookedSlots } = response.data;
          console.log(`Booked Times for ${date}:`, bookedSlots); 
          setAvailableTimes(availableTimes);
          setBookedTimes(bookedSlots);
        })
        .catch((err) => {
          console.log(err.response.data);
          setAvailableTimes([]); // Reset times if there's an error
        });
    } else {
      setAvailableTimes([]);
      setBookedTimes([]);
    }
  }, [selectedDoctor, selectedDate]);

  // Handle manual input fallback when no doctor is selected
  useEffect(() => {
    setManualTimeInput(!selectedDoctor); // Enable manual input if no doctor is selected
  }, [selectedDoctor]);

  const handleTimeChange = (e) => {
    setSelectedTime(e.target.value); // Save selected time
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Assign Appointment Details</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        
        {/* Doctor Selection */}
        <Form.Group controlId="formAssignDoctor">
          <Form.Label>Assign Doctor</Form.Label>
          <Form.Control
            as="select"
            value={selectedDoctor}
            onChange={(e) => setSelectedDoctor(e.target.value)}
          >
            <option value="">Select Doctor</option>
            {alldoctors.map(doctor => (
              <option key={doctor._id} value={doctor._id}>
                {`${doctor.dr_firstName} ${doctor.dr_middleInitial}. ${doctor.dr_lastName}`}
              </option>
            ))}
          </Form.Control>
        </Form.Group>

        {/* Date Selection */}
        <Form.Group controlId="formAssignDate">
          <Form.Label>Assign Date</Form.Label>
          <Form.Control
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </Form.Group>

        {/* Time Selection */}
        <Form.Group controlId="formAssignTime">
          <Form.Label>Assign Time {manualTimeInput ? "(Manual)" : "(From Available Slots)"}</Form.Label>
          {manualTimeInput ? (
            // Manual time input when no doctor is selected
            <Form.Control
              type="time"
              value={selectedTime}
              onChange={handleTimeChange}
            />
          ) : (
            // Show available time slots for the selected doctor and date
            availableTimes.length > 0 ? (
              <div>
                {availableTimes.map(timeSlot => (
                  <Button
                    key={timeSlot}
                    variant="outline-primary"
                    onClick={() => setSelectedTime(timeSlot)}
                    disabled={bookedTimes.includes(timeSlot)} // Disable if the time slot is already booked
                    className="m-1"
                  >
                    {timeSlot}
                  </Button>
                ))}
              </div>
            ) : (
              <p>No available times for the selected date.</p>
            )
          )}
        </Form.Group>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSaveDetails}>
          Save
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AssignAppointmentModal;
